import React, { useState } from "react";
import "./home.css";
import ScheduleModal from "./ScheduleModal";

function Home() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const openModal = () => {
    setIsModalOpen(true);
    setIsDropdownOpen(false); 
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="Home">
      <div className="HomeContainer">
        <div className="HomeTitle">Order Service near you</div>

        <div className="AddressForm">
          <input
            type="text"
            className="AddressInput"
            placeholder="Enter delivery address"
          />

          <div className="DeliverNowDropdown">
            <button className="DropdownButton" onClick={toggleDropdown}>
              Schedule now â–¼
            </button>

            {isDropdownOpen && (
              <div className="DropdownMenu">
                <div className="DropdownItem">Service now</div>
                <div className="DropdownItem" onClick={openModal}>
                  Schedule for later
                </div>
              </div>
            )}
          </div>

          <button className="SearchButton">Search here</button>
        </div>

        <div className="SignIn">
          Or <a href="#">Sign In</a>
        </div>
        {isModalOpen && <ScheduleModal closeModal={closeModal} />}
      </div>
    </div>
  );
}

export default Home;
