import React, { useState, useLayoutEffect, useRef } from "react";
import "../style/home.css";
import ScheduleModal from "./ScheduleModal";
import { gsap } from "gsap";

function Home() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const homeContainerRef = useRef(null);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const openModal = () => {
    setIsModalOpen(true);
    setIsDropdownOpen(false);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Ensure animation happens after DOM updates with useLayoutEffect
  useLayoutEffect(() => {
    const timeline = gsap.timeline();

    // Only animate opacity, not scale, to avoid layout shifting issues
    timeline.fromTo(
      homeContainerRef.current,
      { opacity: 0 },
      { opacity: 1, duration: 1, ease: "power2.out" }
    );

    return () => {
      gsap.to(homeContainerRef.current, {
        opacity: 0, // Focus on fading out instead of scaling
        duration: 2,
        ease: "power2.in",
      });
    };
  }, []);

  return (
    <div>
      <div className="Home">
        <div className="HomeContainer" ref={homeContainerRef}>
          <div className="HomeTitle">Order Service near you</div>

          <div className="AddressForm">
            <input
              type="text"
              className="AddressInput"
              placeholder="Enter delivery address"
            />

            <div className="DeliverNowDropdown">
              <button className="DropdownButton" onClick={toggleDropdown}>
                Schedule now â–¼
              </button>

              {isDropdownOpen && (
                <div className="DropdownMenu">
                  <div className="DropdownItem">Schedule now</div>
                  <div className="DropdownItem" onClick={openModal}>
                    Schedule for later
                  </div>
                </div>
              )}
            </div>

            <button className="SearchButton">
              Search here
            </button>
          </div>

          {/* ScheduleModal is now outside HomeContainer */}
          {isModalOpen && <ScheduleModal closeModal={closeModal} />}
        </div>
      </div>
    </div>
  );
}

export default Home;
