import React, { useState, useRef, useEffect } from "react";
import "../style/home.css";
import ScheduleModal from "./ScheduleModal";
import { gsap } from "gsap";

function Home({ show }) {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const homeContainerRef = useRef(null);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const openModal = () => {
    setIsModalOpen(true);
    setIsDropdownOpen(false);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Animate in when component mounts or show changes
  useEffect(() => {
    const timeline = gsap.timeline();

    if (show) {
      timeline.fromTo(
        homeContainerRef.current,
        { opacity: 0, scale: 0.8 }, // Start small and transparent
        { opacity: 1, scale: 1, duration: 1, ease: "power2.out" } // Expand and fade in
      );
    } else {
      // Fade out and shrink when hiding
      timeline.to(homeContainerRef.current, {
        opacity: 0,
        scale: 0.8,
        duration: 0.8,
        ease: "power2.in",
      });
    }
  }, [show]);

  return (
    <div>
      <div className="Home">
        <div className="HomeContainer" ref={homeContainerRef}>
          <div className="HomeTitle">Order Service near you</div>

          <div className="AddressForm">
            <input
              type="text"
              className="AddressInput"
              placeholder="Enter delivery address"
            />

            <div className="DeliverNowDropdown">
              <button className="DropdownButton" onClick={toggleDropdown}>
                Schedule now â–¼
              </button>

              {isDropdownOpen && (
                <div className="DropdownMenu">
                  <div className="DropdownItem">Schedule now</div>
                  <div className="DropdownItem" onClick={openModal}>
                    Schedule for later
                  </div>
                </div>
              )}
            </div>

            <button className="SearchButton">Search here</button>
          </div>

          {isModalOpen && <ScheduleModal closeModal={closeModal} />}
        </div>
      </div>
    </div>
  );
}

export default Home;
