import React, { useEffect, useRef } from "react";
import { gsap } from "gsap"; // Import GSAP for animation
import "../style/SignUpForm.css";

function SignUpForm({ resetSignup }) {
  const modelRef = useRef();
  const outerSquareRef = useRef();
  const innerHoleRef = useRef();

  useEffect(() => {
    const timeline = gsap.timeline();

    // GSAP animation for outer square spinning and expanding
    timeline.fromTo(
      [outerSquareRef.current, innerHoleRef.current],
      { scale: 0, rotation: 0 }, // Start at scale 0 (completely hidden)
      { scale: 10, rotation: 360 * 1, duration: 8, ease: "power2.out" } // Expand both to scale 10 and rotate
    );

    // GSAP animation for the form fading in and scaling up at the same time
    timeline.fromTo(
      modelRef.current,
      { opacity: 0, scale: 0.5 }, // Start at 0 opacity and small scale
      { opacity: 1, scale: 1, duration: 2, ease: "power2.out" }, // Fade in and scale up
      "-=7" // Start this animation 1.5 seconds before the outer square finishes (overlapping animations)
    );

    const onClick = (event) => {
      if (modelRef && !modelRef.current.contains(event.target)) {
        resetSignup();
      }
    };
    document.addEventListener("mousedown", onClick);

    return () => {
      document.removeEventListener("mousedown", onClick);
    };
  }, [resetSignup]);

  return (
    <div className="SignUpContainer">
      {/* Outer square for the spinning cube effect */}
      <div className="outerSquare" ref={outerSquareRef}>
        {/* Inner hole that grows along with the outer square */}
        <div className="innerHole" ref={innerHoleRef}></div>
      </div>
      <div className="SignUpBox" ref={modelRef}>
        <h2>Sign Up</h2>
        <form action="/signUp" onSubmit={resetSignup}>
          <div className="SignUpInput">
            <label htmlFor="fName">First Name</label>
            <input type="text" id="fName" name="fName" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="lName">Last Name</label>
            <input type="text" id="lName" name="lName" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="age">Age</label>
            <input type="number" id="age" name="age" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" name="password" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <input type="password" id="confirmPassword" name="confirmPassword" required></input>
          </div>

          <button type="submit" className="SignUpBtn">Sign Up</button>
        </form>
      </div>
    </div>
  );
}

export default SignUpForm;
