import React from "react";
import "./style/App.css";
import Home from "./Components/JS/Home.js";
import SearchPage from "./Components/JS/SearchPage.js";
import SearchBar from "./Components/JS/SearchBar.js";
import FastCube from "./Components/JS/FastCube.js";
import Header from "./Components/JS/Header.js";

function App() {
  const [searched, setterSearched] = React.useState(false);
  const [backhome, setterBackhome] = React.useState(true);
  const [authentication, setterAuth] = React.useState(false);
  const [showHome, setShowHome] = React.useState(true); // New state to control Home

  // Called when search is submitted
  const setSearched = () => {
    setterSearched(true); // Set searched to true
    setterBackhome(false);
    setShowHome(false); // Hide Home when searching
  };

  const resetSearch = () => {
    setterSearched(false);
    setterBackhome(true);
    setShowHome(false); // Hide Home first before showing again
    setTimeout(() => {
      setShowHome(true); // Re-show Home after small delay
    }, 100); // Delay to allow animation reset
  };

  const setAuth = () => {
    setterAuth(true);
  };

  const resetAuth = () => {
    setterAuth(false);
  };

  return (
    <div>
      <FastCube />
      <Header backHome={resetSearch} setAuth={setAuth} resetAuth={resetAuth} />

      {/* Conditionally show Home component */}
      {showHome && backhome && <Home />} 

      {/* Show SearchBar or SearchPage based on search state */}
      {!authentication && <SearchBar searched="" onSearchSubmit={setSearched} />}
      {searched && <SearchPage searched="hello" />}
    </div>
  );
}

export default App;
