import React, { useEffect, useRef } from "react";
import ReactDOM from "react-dom";
import "../style/home.css";

function ScheduleModal({ closeModal }) {
  const modalRef = useRef();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        closeModal();
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [closeModal]);

  return ReactDOM.createPortal(
    <div className="ModalOverlay">
      <div className="ModalContent" ref={modalRef}>
        <h2>Pick a time</h2>
        <div className="TimeSelection">
          <label htmlFor="date">Date</label>
          <select id="date">
            <option>Today, Wed, Oct 23</option>
            <option>Tomorrow, Thu, Oct 24</option>
          </select>

          <label htmlFor="time">Time</label>
          <select id="time">
            <option>3:45 AM - 4:15 AM</option>
            <option>4:15 AM - 4:45 AM</option>
          </select>
        </div>
        <button className="ScheduleButton" onClick={closeModal}>
          Schedule
        </button>
        <button className="DeliverNowButton" onClick={closeModal}>
          Schedule now
        </button>
      </div>
    </div>,
    document.getElementById("modal-root") // Use portal to render outside the container
  );
}

export default ScheduleModal;
