import React, { useEffect, useRef } from "react";
import { gsap } from "gsap"; // Import GSAP for animation
import "../style/SignUpForm.css";

function SignUpForm({ resetSignup }) {
  const modelRef = useRef();
  const outerSquareRef = useRef(); // Reference for the green square
  const clearingSquareRef = useRef(); // Reference for the white clearing square

  useEffect(() => {
    const timeline = gsap.timeline();

    // First step: Animate the green square to fill the screen
    timeline.fromTo(
      outerSquareRef.current,
      { scale: 0 }, // Start at scale 0 (completely hidden)
      { scale: 15, duration: 1.5, ease: "power2.out" } // Scale up to fill the screen
    );

    // Second step: Animate the white square to "clear" the screen and reveal the form
    timeline.fromTo(
      clearingSquareRef.current,
      { scale: 0 }, // Start small
      {
        scale: 15,
        duration: 1.5,
        ease: "power2.out",
        onComplete: () => {
          // Make the form visible once the white square finishes expanding
          gsap.to(modelRef.current, { opacity: 1, duration: 0.5 });
        },
      },
      "-=1.2" // Overlap the white square animation slightly with the green square
    );

    const onClick = (event) => {
      if (modelRef && !modelRef.current.contains(event.target)) {
        resetSignup();
      }
    };
    document.addEventListener("mousedown", onClick);

    return () => {
      document.removeEventListener("mousedown", onClick);
    };
  }, [resetSignup]);

  return (
    <div className="SignUpContainer">
      {/* Outer green square that fills the screen */}
      <div className="outerSquare" ref={outerSquareRef}></div>

      {/* White clearing square */}
      <div className="clearingSquare" ref={clearingSquareRef}></div>

      {/* The form that will appear once the white square "clears" */}
      <div className="SignUpBox" ref={modelRef}>
        <h2>Sign Up</h2>
        <form action="/signUp" onSubmit={resetSignup}>
          <div className="SignUpInput">
            <label htmlFor="fName">First Name</label>
            <input type="text" id="fName" name="fName" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="lName">Last Name</label>
            <input type="text" id="lName" name="lName" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="age">Age</label>
            <input type="number" id="age" name="age" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" name="password" required></input>
          </div>

          <div className="SignUpInput">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <input type="password" id="confirmPassword" name="confirmPassword" required></input>
          </div>

          <button type="submit" className="SignUpBtn">Sign Up</button>
        </form>
      </div>
    </div>
  );
}

export default SignUpForm;
