import React, { useEffect, useRef } from "react";
import gsap from "gsap";

export default function FastCube() {
  const cubeRef = useRef(null);

  useEffect(() => {
    let ctx = gsap.context(() => {
      // Spin and move the cube across the screen
      gsap.to(cubeRef.current, {
        duration: 4,
        x: window.innerWidth - 150, // Moves cube to the right of the screen
        y: window.innerHeight / 2 - 50, // Center vertically
        rotation: 360, // Spin the cube
        ease: "power2.inOut",
      });
    });

    return () => ctx.revert(); // Cleanup on component unmount
  }, []);

  return (
    <div
      ref={cubeRef}
      className="cube"
      style={{
        width: "100px",
        height: "100px",
        backgroundColor: "lightblue",
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        perspective: "1000px",
      }}
    >
      <div
        style={{
          width: "100px",
          height: "100px",
          backgroundColor: "blue",
          transformStyle: "preserve-3d",
          transform: "rotateX(45deg) rotateY(45deg)",
        }}
      ></div>
    </div>
  );
}
