import React, { useCallback } from "react";
import { gsap } from "gsap"; 
import "../style/MenuPopOut.css";
import SignInForm from "./SignInForm.js";
import SignUpForm from "./SignUpForm.js";

export default function MenuPopOut({
  resetPopOut,
  login,
  signin,
  setLogin,
  resetLogin,
  setSignup,
  resetSignUp,
}) {
  const modelRef = React.useRef();

  // Memoize handleCloseWithAnimation to avoid re-creation
  const handleCloseWithAnimation = useCallback(() => {
    gsap.to(modelRef.current, {
      x: "-100%",
      duration: 0.5,
      ease: "power2.in",
      onComplete: () => {
        resetPopOut(); 
      },
    });
  }, [resetPopOut]); // Add resetPopOut to dependencies as it's being used

  // Entry animation
  React.useEffect(() => {
    gsap.fromTo(
      modelRef.current,
      { x: "-100%" }, 
      { x: "0%", duration: 0.5, ease: "power2.out" } 
    );
  }, []); 

  // Handle clicking outside the modal
  React.useEffect(() => {
    const handleClickOutside = (event) => {
      if (modelRef.current && !modelRef.current.contains(event.target)) {
        handleCloseWithAnimation(); 
      }
    };
    if (!login && !signin) {
      document.addEventListener("mousedown", handleClickOutside);
    }

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [login, signin, handleCloseWithAnimation]);

  // Close when login or signup is clicked
  React.useEffect(() => {
    if (login || signin) {
      handleCloseWithAnimation(); 
    }
  }, [login, signin, handleCloseWithAnimation]);

  return (
    <div>
      {login && <SignInForm resetLogin={resetLogin} />}
      {signin && <SignUpForm resetSignup={resetSignUp} />}
      <div className="MenuPopOut" ref={modelRef}>
        <div className="MenuPopOutContainer">
          <div className="MPOSignIn" onClick={setSignup}>
            SignUp
          </div>
          <div className="MPOLogIn" onClick={setLogin}>
            LogIn
          </div>
          <div className="MPOHistory">History</div>
          <div className="MPOService">Services</div>
          <div className="MPOAddService">Add Service</div>
        </div>
      </div>
    </div>
  );
}
